// Login.js
import React, { useState } from "react";
import { auth } from "./firebase";
import { signInWithEmailAndPassword } from "firebase/auth";
import "./Login.css"; // Добавляем стили
import tabixIcon from "./icons/tabix.png";
import swnIcon from "./icons/SWN.png";
import dreambleIcon from "./icons/Dreamble.png";

function Login({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(
        auth,
        email,
        password
      );
      onLogin(userCredential.user);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="login-container">
        <div className="login-box">
          
            {/* Добавляем блок с иконками */}
            
            <div className="login-icons">
            <img src={tabikIcon} alt="Tabik Icon" />
    <img src={swnIcon} alt="SWN Icon" />
    <img src={dreambleIcon} alt="Dreamble Icon" />
            </div>

            <h2>Вхід</h2>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Пароль"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Увійти</button>
            </form>
        </div>
    </div>
);

}

export default Login;
